widgets.script:
var start = Date.now()

function stime(t){
  return [t.getHours(), t.getMinutes()].map(x=>String(x).padStart(2, "0")).join("h")
}

set("timer", stime(new Date()) + " / 00:00:00")

setInterval(()=>{
var now = new Date(),
    diff = new Date(now - start),
    sdiff = [diff.getHours() - 1, diff.getMinutes(), diff.getSeconds()].map(x=>String(x).padStart(2, "0")).join(":")


set("timer", stime(now) + " / " + sdiff)



}, 1000)

