}}

widgets.script:
function back(){
 set("bigup", get("bigup").substr(0, get("bigup").length-1))
}
if (value == 0) {
 clearInterval()
 clearTimeout()
} else {
 back()
 setTimeout(()=>{
  setInterval(()=>{
   back()
  }, 50)
 }, 500)
}

