widgets.filters:
JS{{
var f = {},
    r = [f],
    cat = @{zynstrip_globalfilter_category_@{synthselect}},
    type = @{zynstrip_globalfilter_type_@{synthselect}},
    q = @{zynstrip_globalfilter_q_@{synthselect}},
    _types = [
      ["lowpass", "highpass", "lowpass", "highpass", "bandpass", "notch", "peak", "lowshelf", "highshelf"],
      [],
      ["lowpass", "highpass", "bandpass", "notch", "peak"]
    ]

if (cat !== 1) {
 f.q = q/127 + 0.5
 f.type =  _types[cat][type]
 f.freq = @{zynstrip_globalfilter_freq_@{synthselect}}
} else {
 f.type = "bandpass"
 f.freq = @{zynstrip_globalfilter_formantcenterfreq_@{synthselect}}
}
f.freq = f.freq / 127 * 21980 + 20


return r
}}

