widgets.script:
var start = Date.now()
set("timer", "00:00:00")

setInterval(()=>{
var now = new Date(),
    snow = [now.getHours(), now.getMinutes()].map(x=>String(x).padStart(2, "0")).join("h"),
    diff = new Date(now - start),
    sdiff = [diff.getHours() - 1, diff.getMinutes(), diff.getSeconds()].map(x=>String(x).padStart(2, "0")).join(":")



set("clock", snow)

set("timer", sdiff)


}, 1000)

widgets.values:
JS{{
var tabs = @{center_tabs.tabs},
    values = {}

for (var i in tabs) {
  values[tabs[i].label] = parseInt(i)
}

return values

}}

widgets.script:
function back(){
 set("bigup", get("bigup").substr(0, get("bigup").length-1))
}
if (value == 0) {
 clearInterval()
 clearTimeout()
} else {
 back()
 setTimeout(()=>{
  setInterval(()=>{
   back()
  }, 50)
 }, 500)
}

