var props = {},
    data = OSC{/MonitorsORL}

props.widgetId = 'monitor_strip'
props.props = {}
props.props.variables = {
  strip: data[$].id,
  value: data[$].value,
  monitor: 'MonitorsORL',
  target: '127.0.0.1:7030'
}

if ($ == @{this.quantity} -1) props.props.colorWidget = 'white' 

return props

}}

