widgets.props:
JS{{

var strips=@{this.variables.strips}
var props={props:{}}
props.props.visible = true
props.widgetId = 'ecastrip'
props.props.variables = {
 target: @{this.variables.target},
 name: strips[$],
}
if (@{this.variables}.muted[strips[$]]) {
 props.props.variables.muted = 1
}
return props

}}

widgets.script:
var q = getProp('keyboard_nosustain', 'keys')
var s = getProp('keyboard_nosustain', 'start')
var i = 0
if (value) {
  setInterval(()=>{
    if (i) set('keyboard_nosustain/' + i, 0)
    i = Math.round(Math.random()  * q + s)
    set('keyboard_nosustain/' + i, 1)
  }, 100)

} else {
  clearInterval()
}

